import { createStore } from 'vuex'
import router from '@/router/index'

const state = {};
const getters = {};
const mutations = {
  redirectTo(state, payload) {
    router.push({ name: "home" });
  }
};
const actions = {
  redirectTo({ commit }) {
    commit("redirectTo");
  },
};

export default createStore({
  state,
  getters,
  mutations,
  actions,
  modules: {
  }
})
